import React, { Component } from 'react'
import Web3 from 'web3'
import Marketplace from '../abis/Marketplace.json'
import Main from '../components/Main'
import AddProduct from '../components/AddProduct'
import Product from '../components/Products'
import Signin from '../components/Signin'

export default class Home extends Component {
    constructor(props) {
      super(props);
      this.state = {
        account: "",
        productCount: 0,
        products: [],
        loading: true,
      };

      this.createProduct = this.createProduct.bind(this);
      this.purchaseProduct = this.purchaseProduct.bind(this);
    }
    async componentWillMount() {
      await this.loadWeb3();
      await this.loadBlockchainData();
    }

    async loadWeb3() {
      if (window.ethereum) {
        window.web3 = new Web3(window.ethereum);
        await window.ethereum.enable();
      } else if (window.web3) {
        window.web3 = new Web3(window.web3.currentProvider);
      } else {
        window.alert(
          "Non-Ethereum browser detected. You should consider trying MetaMask!"
        );
      }
    }

    async loadBlockchainData() {
      const web3 = window.web3;
      // Load account
      const accounts = await web3.eth.getAccounts();
      localStorage.setItem("account", accounts[0]);
      this.setState({ account: accounts[0] });
      const networkId = await web3.eth.net.getId();
      const networkData = Marketplace.networks[networkId];
      if (networkData) {
        const marketplace = web3.eth.Contract(
          Marketplace.abi,
          networkData.address
        );
        this.setState({ marketplace });
        const productCount = await marketplace.methods
          .productCount()
          .call();
        this.setState({ productCount });
        // Load products
        for (var i = 1; i <= productCount; i++) {
          const product = await marketplace.methods
            .products(i)
            .call();
          this.setState({
            products: [...this.state.products, product],
          });
        }
        this.setState({ loading: false });
      } else {
        window.alert(
          "Marketplace contract not deployed to detected network."
        );
      }
    }

    createProduct(name, price) {
      this.setState({ loading: true });
      this.state.marketplace.methods
        .createProduct(name, price)
        .send({ from: this.state.account })
        .once("receipt", (receipt) => {
          this.setState({ loading: false });
        });
    }
    purchaseProduct(id, price) {
      this.setState({ loading: true });
      this.state.marketplace.methods
        .purchaseProduct(id)
        .send({ from: this.state.account, value: price })
        .once("receipt", (receipt) => {
          this.setState({ loading: false });
        });
    }

    render() {
      return (
        <div>
          <div className="container-fluid">
            <div className="row">
              <main>
                <Main />
                <AddProduct createProduct={this.createProduct} />
                {this.state.loading ? (
                  <div id="loader" className="text-center">
                    <p className="text-center">Loading...</p>
                  </div>
                ) : (
                  <Product
                    products={this.state.products}
                    purchaseProduct={this.purchaseProduct}
                  />
                )}
              </main>
            </div>

          </div>
        </div>
      );
    }
  }
