import { Remove } from '@material-ui/icons';
import React, { Component } from 'react'
import '../css/Product.css';

export default class Products extends Component {
    convertMoney=(price)=>{
        return window.web3.utils.fromWei(price.toString(),"Ether")
    }
    renderBuyButton=(item)=>{
        if(!item.purchased){
            return (
              <div class="snipcart-details top_brand_home_details">
                <form action="#" method="post">
                  <fieldset>
                    <button
                      style={{ width: "100%" }}
                      name={item.id}
                      value={item.price}
                      onClick={(event) => {
                        this.props.purchaseProduct(
                          event.target.name,
                          event.target.value
                        );
                      }}
                      className="button"
                    >
                      Buy
                    </button>
                  </fieldset>
                </form>
              </div>
            );
        }
    }

    renderProductBox=()=>{
        if(this.props.products){
            return this.props.products.map((item,index)=>{
                return (
                  <div class="col-md-4 top_brand_left">
                    <div class="hover14 column">
                      <div class="agile_top_brand_left_grid">
                        <div class="agile_top_brand_left_grid1">
                          <figure>
                            <div class="snipcart-item block" >
                              <div class="snipcart-thumb">
                                <a href="#"><img title=" " alt=" " src="../Image/1.png" /></a>
                                <div className="product-id" style={{ opacity: 0}}>{item.id.toString()}</div>	
                                <p>{item.name}</p>
                                <h4>{this.convertMoney(item.price)} Eth</h4>
                                <div className="product-owner">{item.owner}</div>
                              </div>
                              <div class="snipcart-details top_brand_home_details">
                                <form action="#" method="post">
                                  <fieldset>
                                    <input type="submit" name="submit" value="Add to cart" class="button" />
                                  </fieldset>
                                </form>
                              </div>
                            </div>
                          </figure>
                          {this.renderBuyButton(item)}
                        </div>
                      </div>
                    </div>
                  </div>
                );
            })

        }else{
            return (<div></div>)
        }
    }
    render() {
        return (
          <div className="my-2">
            <h1>Product</h1>
            <div className="container">
                <div className="row">
                    {this.renderProductBox()}
                </div>
            </div>
          </div>
        );
    }
}
