                <Modal.Title>Add Product</Modal.Title>
              </Modal.Header>
              <Modal.Body>
                <form>
                  <div className="form-group mr-sm-2">
                    <input
                      id="productName"
                      type="text"
                      ref={(input) => {
                        this.productName = input;
                      }}
                      className="form-control"
                      placeholder="Product Name"
                      required
                    />
                  </div>
                  <div className="form-group mr-sm-2">
                    <input
                      id="productPrice"
                      type="text"
                      ref={(input) => {
                        this.productPrice = input;
                      }}
                      className="form-control"
                      placeholder="Product Price"
                      required
                    />
                  </div>
                  <div className="form-group mr-sm-2">
                    <input
                      id="imgProduct"
                      type="file"
                      ref={(input) => {
                        this.imgProduct = input;
                      }}
                      required
                    />
                  </div>
                  <button type="submit" className="btn btn-primary">
                    Add Product
                  </button>
                </form>
              </Modal.Body>
              <Modal.Footer>
                <Button variant="secondary" onClick={this.closeAddProductDialog}>
                  Close
                </Button>
                <Button variant="primary" onClick={(e)=>{this.handleAddProduct(e)}}>
