                </Button>
              </Modal.Footer>
            </Modal> */}
            {/* <Button onClick={this.openAddProductDialog}></Button> */}
            <p>&nbsp;</p>
            <h2>Buy Product</h2>
            <table className="table">
              <thead>
                <tr>
                  <th scope="col">#</th>
                  <th scope="col">Img</th>
                  <th scope="col">Name</th>
                  <th scope="col">Price</th>
                  <th scope="col">Owner</th>
                  <th scope="col"></th>
                </tr>
              </thead>
              <tbody id="productList">
                {this.props.products.map((product, key) => {
                  return (
                    <tr key={key}>
                      <th scope="row">{product.id.toString()}</th>
                      <th>Img</th>
                      <td>{product.name}</td>
                      <td>
                        {window.web3.utils.fromWei(
                          product.price.toString(),
                          "Ether"
                        )}{" "}
                        Eth
                      </td>
                      <td>{product.owner}</td>
                      <td>
                        {!product.purchased ? (
                          <button
                            name={product.id}
                            value={product.price}
                            onClick={(event) => {
                              this.props.purchaseProduct(
                                event.target.name,
                                event.target.value
                              );
                            }}
                            className="btn btn-primary"
                          >
                            Buy
                          </button>
                        ) : null}
                      </td>
                    </tr>
                  );
                })}
              </tbody>
            </table>
          </div>
        );
    }
}

export default Product;
