                        )}
                        Eth
                      </div>
                      <div className="product-owner">{item.owner}</div>
                    </div>
                    <div className="product-buy">
                      {!item.purchased ? (
                        <button
                          name={item.id}
                          value={item.price}
                          onClick={(event) => {
                            this.props.purchaseProduct(
                              event.target.name,
                              event.target.value
                            );
                          }}
                          className="btn btn-primary"
                        >
                          Buy
