import { Remove } from '@material-ui/icons';
import React, { Component } from 'react'

export default class Products extends Component {
    convertMoney=(price)=>{
        return window.web3.utils.fromWei(price.toString(),"Ether")
    }
    renderBuyButton=(item)=>{
        if(!item.purchased){
            return (
              <button
              style={{width:"100%"}}
                name={item.id}
                value={item.price}
                onClick={(event) => {
                  this.props.purchaseProduct(
                    event.target.name,
                    event.target.value
                  );
                }}
                className="btn btn-primary"
              >
                Buy
              </button>
            );
        }
    }

    handleDelete = (item) => {
      const newsId = item.id;
      this.setState(prevState => ({
        products: prevState.products.filter(elm => elm.id !== newsId )
      }));
    }

    renderDeleteButton=(item)=>{
        if(item.purchased){
            return (
                <button onClick={() => {this.handleDelete(item)}}
                className="btn btn-primary"
                >Delete
                </button>
            )
        }
    }

    renderProductBox=()=>{
        if(this.props.products){
            return this.props.products.map((item,index)=>{
                return (
                  <div
                    // key={index}
                    className="col-lg-4 product-box"
                    style={{ height: "45vh", padding: "10px" }}
                  >
                    <div className="product-img" style={{ height: "70%",
                    backgroundImage:"url(https://images.unsplash.com/photo-1575224300306-1b8da36134ec?ixlib=rb-1.2.1&ixid=eyJhcHBfaWQiOjEyMDd9&w=1000&q=80)" }}>                      
                    </div>
                    <div
                      className="product-detail text-center"
                      style={{ height: "30%" }}
                    >
                      <div className="product-id" style={{ opacity: 0}}>{item.id.toString()}</div>
                      <div className="product-name">{item.name}</div>
                      <div className="product-price">
                         {this.convertMoney(item.price)} Eth
                      </div>
                      <div className="product-owner">{item.owner}</div>
                        {this.renderBuyButton(item)}
                        {this.renderDeleteButton(item)}
                    </div>
                  </div>
                );
            })

        }else{
            return (<div></div>)
        }
    }
    render() {
        return (
          <div className="my-2">
            <h1>Product</h1>
            <div className="container">
                <div className="row">
                    {this.renderProductBox()}
                </div>
            </div>
          </div>
        );
    }
}
