import React, { Component } from 'react';
import Modal from "react-bootstrap/Modal";
import ModalBody from "react-bootstrap/ModalBody";
import ModalHeader from "react-bootstrap/ModalHeader";
import ModalFooter from "react-bootstrap/ModalFooter";
import ModalTitle from "react-bootstrap/ModalTitle";
import Button from 'react-bootstrap/Button'
class Product extends React.Component {
    constructor(props){
      super(props)
      this.state={
        showModalAddProduct:false
      }
    }
    openAddProducModal=()=>{
      this.setState({
        showModalAddProduct:true
      })
    }
    closeAddProducModal=()=>{
      this.setState({
        showModalAddProduct:false
      })
    }
    addProduct=(event)=>{
      event.preventDefault()
      const name = this.productName.value
      const price = window.web3.utils.toWei(this.productPrice.value.toString(), 'Ether')
      this.props.createProduct(name, price)
    }
    render(){
        return (
          <div>
            <h1>Add Product</h1>
            <Modal
              show={this.state.ModalshowModalAddProduct}
              onHide={this.closeAddProducModal}
            >
              <ModalHeader closeButton>
                <ModalTitle>Modal heading</ModalTitle>
              </ModalHeader>
              <ModalBody>
                <form>
                  <div className="form-group mr-sm-2">
                    <input
                      id="productName"
                      type="text"
                      ref={(input) => {
                        this.productName = input;
                      }}
                      className="form-control"
                      placeholder="Product Name"
                      required
                    />
                  </div>
                  <div className="form-group mr-sm-2">
                    <input
                      id="productPrice"
                      type="text"
                      ref={(input) => {
                        this.productPrice = input;
                      }}
                      className="form-control"
                      placeholder="Product Price"
                      required
                    />
                  </div>
                  <div className="form-group mr-sm-2">
                    <input
                      id="imgProduct"
                      type="file"
                      ref={(input) => {
                        this.imgProduct = input;
                      }}
                      required
                    />
                  </div>
                  <button type="submit" className="btn btn-primary">
                    Add Product
                  </button>
                </form>
              </ModalBody>
              <ModalFooter>
                <Button variant="secondary" onClick={this.closeAddProducModal}>
                  Close
                </Button>
                <Button variant="primary" onClick={(e)=>{
                  this.addProduct(e)
                }}>
                  Add
                </Button>
              </ModalFooter>
            </Modal>
            <Button onClick={this.openAddProducModal}>
              Add Product
            </Button>
            <p>&nbsp;</p>
            <h2>Buy Product</h2>
            <table className="table">
              <thead>
                <tr>
                  <th scope="col">#</th>
                  <th scope="col">Img</th>
                  <th scope="col">Name</th>
                  <th scope="col">Price</th>
                  <th scope="col">Owner</th>
                  <th scope="col"></th>
                </tr>
              </thead>
              <tbody id="productList">
                {this.props.products.map((product, key) => {
                  return (
                    <tr key={key}>
                      <th scope="row">{product.id.toString()}</th>
                      <th>Img</th>
                      <td>{product.name}</td>
                      <td>
                        {window.web3.utils.fromWei(
                          product.price.toString(),
                          "Ether"
                        )}{" "}
                        Eth
                      </td>
                      <td>{product.owner}</td>
                      <td>
                        {!product.purchased ? (
                          <button
                            name={product.id}
                            value={product.price}
                            onClick={(event) => {
                              this.props.purchaseProduct(
                                event.target.name,
                                event.target.value
                              );
                            }}
                            className="btn btn-primary"
                          >
                            Buy
                          </button>
                        ) : null}
                      </td>
                    </tr>
                  );
                })}
              </tbody>
            </table>
          </div>
        );
    }
}

export default Product;
