import React, { Component } from 'react'

export default class Products extends Component {
    convertMoney=(price)=>{
        return window.web3.utils.fromWei(price.toString(),"Ether")
    }
    renderProductBox=()=>{
        console.log(this.props.products)
        if(this.props.products){
            return this.props.products.map((item,index)=>{
                return (
                  <div
                    // keys={index}
                    className="col-lg-3 product-box"
                    style={{ height: "20vh" }}
                  >
                    <img src="" alt="pic" />
                    <div className="product-detail">
                      <div className="product-id">{item.id}</div>
                      <div className="product-price">
                        {this.convertMoney(item.price)}
                        Eth
                      </div>
                      <div className="product-owner">{item.owner}</div>
                    </div>
                    <div className="product-buy">
                      {!item.purchased ? (
                        <button
                          name={item.id}
                          value={item.price}
                          onClick={(event) => {
                            this.props.purchaseProduct(
                              event.target.name,
                              event.target.value
                            );
                          }}
                          className="btn btn-primary"
                        >
                          Buy
                        </button>
                      ) : null}
                    </div>
                  </div>
                );
            })
        }else{
            return (<div></div>)
        }
    }
    render() {
        return (
          <div>
            <h2>Buy Product</h2>
            <div className="container">
                <div className="row">
                    {this.renderProductBox()}
                </div>
            </div>
          </div>
        );
    }
}
