import React, { Component } from 'react'

export default class Products extends Component {
    convertMoney=(price)=>{
        return window.web3.utils.fromWei(price.toString(),"Ether")
    }
    renderBuyButton=(item)=>{
        if(!item.purchased){
            return (
              <button
              style={{width:"100%"}}
                name={item.id}
                value={item.price}
                onClick={(event) => {
                  this.props.purchaseProduct(
                    event.target.name,
                    event.target.value
                  );
                }}
                className="btn btn-primary"
              >
                Buy
              </button>
            );
        }else{
            return (<> </>)
        }
    }
    renderProductBox=()=>{
        console.log(this.props.products)
        if(this.props.products){
            return this.props.products.map((item,index)=>{
                return (
                  <div
                    // key={index}
                    className="col-lg-4 product-box"
                    style={{ height: "20vh",padding:"10px" }}
                  >
                    <img 
                        src="https://images.unsplash.com/photo-1603486000993-06849ef87b94?ixlib=rb-1.2.1&ixid=eyJhcHBfaWQiOjEyMDd9&auto=format&fit=crop&w=700&q=80" 
                        alt="pic" 
                        style={{width:"120px"}}
                    />
                    <div className="product-detail text-center">
                      <div className="product-id">{item.id.toString()}</div>
                      <div className="product-price">
                        {this.convertMoney(item.price)} Eth
                      </div>
                      <div className="product-owner">{item.owner}</div>
                    </div>
                    <div className="product-buy">
                      {this.renderBuyButton(item)}
                    </div>
                  </div>
                );
            })

        }else{
            return (<div></div>)
        }
    }
    render() {
        return (
          <div className="my-2">
            <h2>Buy Product</h2>
            <div className="container">
                <div className="row">
                    {this.renderProductBox()}
                </div>
            </div>
          </div>
        );
    }
}
